import React, { useContext, useEffect } from 'react';
import ThemeContext from "../../context/ThemeContext";

import {StyleSheet, Text, View, Button} from 'react-native';

const HomeScreen = ({navigation}) => {
    console.log(ThemeContext);
    const {style, updateContext} = useContext(ThemeContext);
    console.log(style, updateContext);

    useEffect(() => {
        updateContext((prev) => {
            console.log(prev);
            return {
                ...prev,
                style : {
                    headerStyle: 'red',
                    headerTintColor: 'red'
                }
            }
        })
    }, []);
    return (
        <View style={styles.container}>
          <Text>Home</Text>
          <Button title="About Us" onPress={() => navigation.navigate('About')} />
          <Button title="Update Context" onPress={() => updateContext((prev) => {
                console.log(prev);
                return {
                    ...prev,
                    style : {
                      headerStyle: 'red',
                      headerTintColor: 'red'
                    }
                }
          })} />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default HomeScreen;
